(56, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-03-04 16:42:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'printful_promo_q1_2025', 'marketing', 'en_US', 'New: sell print-on-demand products, risk-free', 'Expand your product line with Printful, now available in the WooCommerce Marketplace. Sell custom products with no upfront costs or inventory. This trusted supplier prints, packages, and ships for you, so you can focus on growing your store — and your next big idea.', '{}', 'pending', 'woocommerce.com', '2025-03-04 16:42:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-03-04 16:42:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Chọn giao diện?', 'Kiểm tra các giao diện tương thích với WooCommerce và chọn một giao diện phù hợp với thương hiệu và nhu cầu kinh doanh của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-06 16:03:21', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cách tùy chỉnh danh mục sản phẩm của bạn', 'Bạn muốn danh mục sản phẩm và hình ảnh của mình trông đẹp mắt và phù hợp với thương hiệu của bạn. Hướng dẫn này sẽ cung cấp cho bạn tất cả các mẹo bạn cần để sản phẩm của bạn trông đẹp mắt trong cửa hàng của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-06 16:03:21', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bạn đã sẵn sàng để ra mắt cửa hàng của mình?', 'Để đảm bảo bạn không bao giờ có cảm giác chìm đắm \"mình đã quên điều gì\", chúng tôi đã tập hợp danh sách kiểm tra trước khi ra mắt cần thiết.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-06 16:03:21', NULL, 0, 'plain', '', 0, 0, 'info');

